    &lt;meta http-equiv=&quot;Content-Language&quot; content=&quot;tr&quot;/&gt;
    &lt;link href=&quot;/zopsedu-templates/static/assets/css/zopsedu-sablon.css&quot; type=&quot;text/css&quot; rel=&quot;stylesheet&quot;/&gt;
&lt;/head&gt;
&lt;body lang=&quot;en-TR&quot; dir=&quot;ltr&quot;&gt;
&lt;div class=&quot;page-container&quot;&gt;
    &lt;div class=&quot;sablon-space&quot;&gt;
        &lt;table class=&quot;header-style&quot;&gt;
            &lt;tr&gt;
                &lt;td class=&quot;zopsedu-header&quot;&gt;
                    LOGO RESMİ
                &lt;/td&gt;
                &lt;td class=&quot;zopsedu-header-middle&quot;&gt;
                    &lt;p class=&quot;sablon-text-style-bold&quot; align=&quot;center&quot;&gt;
                        BİLİMSEL ARAŞTIRMA PROJESİ SONU&Ccedil; RAPORU
                    &lt;/p&gt;
                &lt;/td&gt;
                &lt;td class=&quot;zopsedu-header&quot; valign=&quot;top&quot;&gt;
                &lt;/td&gt;
            &lt;/tr&gt;
        &lt;/table&gt;
    &lt;/div&gt;
    &lt;div class=&quot;sablon-space&quot;&gt;
        &lt;table class=&quot;table-style&quot;&gt;
            &lt;tbody&gt;
            &lt;tr&gt;
