                &lt;td width=&quot;75%&quot;&gt;{{proje.proje_no}}&lt;/td&gt;
            &lt;/tr&gt;
            &lt;tr&gt;
                &lt;td width=&quot;25%&quot; class=&quot;sablon-header&quot;&gt;Projenin Adı&lt;/td&gt;
