            &lt;tr&gt;
                &lt;td width=&quot;25%&quot; class=&quot;sablon-header&quot;&gt;Projenin Toplam &Ouml;deneği&lt;/td&gt;
                &lt;td width=&quot;75%&quot;&gt;{{proje.kabul_edilen_butce}}&lt;/td&gt;
            &lt;/tr&gt;
            &lt;tr&gt;
                &lt;td width=&quot;25%&quot; class=&quot;sablon-header&quot;&gt;Varsa Alınan Ek &Ouml;denek&lt;/td&gt;
                &lt;td width=&quot;75%&quot;&gt;-&lt;/td&gt;
            &lt;/tr&gt;
            &lt;/tbody&gt;
        &lt;/table&gt;
    &lt;/div&gt;
    &lt;div class=&quot;sablon-space&quot;&gt;
        &lt;table class=&quot;table-style&quot;&gt;
            &lt;tbody&gt;
            &lt;tr class=&quot;sablon-header&quot;&gt;
                &lt;td width=&quot;100%&quot; colspan=5 class=&quot;baslik&quot;&gt;
                    PROJE S&Uuml;RESİ
                &lt;/td&gt;
            &lt;/tr&gt;
            &lt;tr class=&quot;sablon-header&quot;&gt;
                &lt;td width=&quot;20%&quot;&gt;Başlama Tarihi&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; Bitiş Tarihi&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; Proje S&uuml;resi&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; Onaylanan Ek S&uuml;re&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; Ek S&uuml;re Dahil Bitiş Tarihi&lt;/td&gt;
            &lt;/tr&gt;
            &lt;tr&gt;
                &lt;td width=&quot;20%&quot;&gt; {{ datetime_to_string(proje.kabul_edilen_baslama_tarihi) if
                    proje.kabul_edilen_baslama_tarihi else &quot;-&quot; }}
                &lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; {{ datetime_to_string(proje.bitis_tarihi) if proje.bitis_tarihi else &quot;-&quot;}}&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; {{proje.proje_suresi}} {{proje.proje_suresi_birimi.value}}&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; -&lt;/td&gt;
                &lt;td width=&quot;20%&quot;&gt; -&lt;/td&gt;
            &lt;/tr&gt;
            &lt;/tbody&gt;
        &lt;/table&gt;
    &lt;/div&gt;
    &lt;div class=&quot;sablon-space&quot;&gt;
        &lt;table class=&quot;table-style&quot;&gt;
            &lt;tbody&gt;
            &lt;tr&gt;
                &lt;td width=&quot;100%&quot; class=&quot;sablon-header&quot;&gt;A. PROJE RAPORU &lt;br/&gt;
                    (Proje raporu aşağıdaki başlıklar dikkate alınarak hazırlanmalıdır)
                &lt;/td&gt;
            &lt;/tr&gt;
            &lt;tr&gt;
